import React, {useEffect} from 'react';
import IconButton from "@material-ui/core/IconButton";
import {Badge} from "@material-ui/core";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import MenuIcon from "@material-ui/icons/Menu";
import { getProductInCart,  getUserId } from '../../reducks/users/selectors'
import { useSelector } from 'react-redux';
import { db }  from '../../firebase/index'

const HeaderMenu = (props) => {
  // カート内の商品数を種と得する
  const selector = useSelector(state => state)
  let productInCart = getProductInCart(selector)

  // ログインユーザーの取得
  const uid =  getUserId(selector)

  // firestoreにリスナーの設定
  useEffect(()=>{
    const nusubscribe = db.collection('users').doc(uid).collection('cart')
  })

  return(
      <>
        <IconButton  >
          <Badge badgeContent={productInCart.length} color="secondary">
          < ShoppingCartIcon />
          </Badge>
        </IconButton>
        <IconButton  >
          <FavoriteBorderIcon />
        </IconButton>
        <IconButton   onClick={(e) => props.handleDrawerToggle(e, true)}>
          <MenuIcon />
        </IconButton>
      </>
  )
}

export default HeaderMenu
