import * as Actions from './actions'
import initialState from '../store/initialState'

export const UserReducer = (state=initialState.users, action) => {
  switch(actions.type){
    case Actions.SIGN_IN:
      return{
        ...state,
        ...action.payload
      }
    default:
      return state
  }
}
