import {CardElement} from '@stripe/react-stripe-js';

const headers = new Headers();
headers.set('Content-type', 'application/json');
const BASE_URL = "https://ec-app-8ba0b.web.app/"

const createCustomer = async(email, paymentMehotdId, uid) => {
    const response = await fetch(BASE_URL + 'v1/customer', {
      method: 'POST',
      headers: headers,
      body: JSON.stringify({
        email: email,
        paymentMehotd: paymentMehotdId,
        uid: uid
      })
    })

    const customerResponse = await response.json();
    return JSON.parse(customerResponse.body)
}

export const registerCard = (stripe, elements) => {
  return async (dispatch, getState)=> {
    const user = getState().users;
    const email= user.email
    const uid = user.id
    const username = user.name

    if (!stripe || !elements) {
      // Stripe.js has not loaded yet. Make sure to disable
      // form submission until Stripe.js has loaded.
      return;
    }

    // Get a reference to a mounted CardElement. Elements knows how
    // to find your CardElement because there can only ever be one of
    // each type of element.
    const cardElement = elements.getElement(CardElement);

    // Use your card Element with other Stripe.js APIs
    const {error, paymentMethod} = await stripe.createPaymentMethod({
      type: 'card',
      card: cardElement,
    });

    if (error) {
      console.log('error')
      return
    } 

    const paymentMehotdId = paymentMethod.id;
    const customerData = await createCustomer(email, paymentMehotdId, uid)

  }
}
