    <div className="u-text-center">
       <PrimaryButton label={"カード情報を登録"} 
       onClick={()=>register()}/>
       <div className="module-spacer--small" />
       <GreyButton label={"マイページに戻る"} 
       onClick={()=>{dispatch(push('/user/mypage'))}}/>
    </div>
    </section>
  )
}

export default PaymentEdit
